func TestImagesUpdate(t *testing.T) {
	picMap := map[int]string{ // picMap[courseID] == link to external image / encode64 (I don't really know why)
		11: "data:image/jpeg;base64,",
		12: "data:image/jpeg;base64,",
		13: "data:image/jpeg;base64,",
		14: "data:image/jpeg;base64,",
		15: "data:image/jpeg;base64,",
		16: "data:image/jpeg;base64,",
		17: "data:image/png;base64,",
		18: "https://i.ytimg.com/vi/CIMmK86vNYo/maxresdefault.jpg",
	}

	resp, err := http.DefaultClient.Get("http://localhost:10110/courses/get-all")
	assert.NoError(t, err)
	defer func() {
		if err = resp.Body.Close(); err != nil {
			log.Print(err)
		}
	}()

	var courses []courseStruct
	err = json.NewDecoder(resp.Body).Decode(&courses)
	assert.NoError(t, err)

	for _, course := range courses {
		course.Body.Src = picMap[course.ID]

		reqBody := &bytes.Buffer{}
		err = json.NewEncoder(reqBody).Encode(course)
		assert.NoError(t, err)

		url := fmt.Sprint("http://localhost:10110/courses/update/", course.ID)

		req, err := http.NewRequest(http.MethodPost, url, reqBody)
		assert.NoError(t, err)

		req.Header.Set("Content-Type", "application/json")

		resp, err = http.DefaultClient.Do(req)
		assert.NoError(t, err)
		assert.Equal(t, 200, resp.StatusCode)
	}
}
